// CREATE A NEW EXPRESS ROUTE
const router = require("express").Router();

const { Router } = require("express");

//IMPORT OUR MODEL
const Coffee = require("../models/coffee");

// SEED DATA FOR SEED ROUTE
const coffeeSeed = [
  {
    name: "Columbian",
    img: "data:image/jpeg;base64,",
    description: "A bright roast with blackberry undertones",
  },
  {
    name: "Ethiopian",
    img: "data:image/jpeg;base64,",
    description: "A bold roast with hints of caramel and chocolate",
  },
  {
    name: "Jamaican",
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS_QYJDGpMk3md5fjLWnMwNm0jCLFBegmLsoA&usqp=CAU",
    description: "A medium roast that leaves a warm, cozy feeling. Tastes of marshmellow and graham crackers",
  },
];

// ROUTES (async, since database actions are asynchronous)

// Seed Route for Seeding Database
router.get("/seed", async (req, res) => {
  // try block for catching errors
  try {
    // remove all places from database
    await Coffee.remove({});
    // add the seed data to the database
    await Coffee.create(coffeeSeed);
    // get full list of places to confirm seeding worked
    const coffees = await Coffee.find({});
    // return full list of places as JSON
    res.json(coffees);
  } catch (error) {
    // return error as JSON with an error status
    res.status(400).json(error);
  }
});

//INDEX ROUTE 
router.get('/', async(req, res) => { 
    try { 
        const coffees = await Coffee.find({})
        res.json(coffees)
    } catch (error) { 
        res.status(400).json(error)
    }
})

router.post('/', async (req, res) => {
try { 
    const newCoffee = await Coffee.create(req.body)
      res.json(newCoffee)
    }  catch (error) { 
        res.status(400).json(error)
    } 
})

router.put('/:id', async (req, res) => { 
    try { 
        const updateCoffee = await Coffee.findByIdAndUpdate(req.params.id, req.body, {new:true})
        res.json(updateCoffee)
    }catch (error) { 
        res.status(400).json(error)
    }
})

router.delete('/:id', async (req, res) => { 
    try { 
        const deleteCoffee = await Coffee.findByIdAndRemove(req.params.id)
        res.json(deleteCoffee)
    } catch (error) { 
        res.status(400).json(error)
    }
})

// export the router which has all our routes registered to it
module.exports = router;